mapper.update:
UPDATE mlsql_job
SET status = #{status}, finish_at = #{finishAt}
WHERE `name` = #{jobName} AND mlsql_user_id = #{userId}

UPDATE mlsql_job
SET status = #{status}, response = #{response}, finish_at = #{finishAt}
WHERE `name` = #{jobName}

UPDATE mlsql_job
SET status = #{status}, reason = #{reason}, finish_at = #{finishAt}
WHERE `name` = #{jobName}
