mapper.update:
update t_workspace_member_infos
set state = '0'
where space_member_id = #{userId}

update t_workspace_member_infos
set state = '1'
where space_member_id = #{userId}
     and  space_id = #{spaceId}

mapper.delete:
delete from t_workspace_member_infos
where space_member_id = #{userId}
     and  space_id = #{spaceId}

mapper.select:
select distinct(space_member_id)
from t_workspace_member_infos
where  space_id = #{spaceId}

select *
from t_workspace_member_infos
where space_member_id = #{memberId}
     and  space_id = #{spaceId}
