mapper.update:
update t_workspace_member_infos
set state = '0'
where space_member_id = #{userId}

update t_workspace_member_infos
set state = '1'
where space_member_id = #{userId}
     and  workspace_id = #{spaceId}

mapper.delete:
delete from t_workspace_member_infos
where space_member_id = #{userId}
     and  workspace_id = #{spaceId}
